function headerMenu(){const e=document.querySelectorAll(".header-nav__list-item"),t=document.querySelectorAll(".dropdown-menu");for(let n=0;n<e.length;n++)e[n].addEventListener("mouseover",(()=>{t[n].classList.add("visible")})),e[n].addEventListener("mouseout",(()=>{t[n].classList.remove("visible")}))}headerMenu();