function headerMenu(){const e=document.querySelectorAll(".header-nav__list-item"),t=document.querySelectorAll(".dropdown-menu");for(let c=0;c<4;c++)e[c].addEventListener("mouseover",(()=>{t[c].classList.add("visible")})),e[c].addEventListener("mouseout",(()=>{t[c].classList.remove("visible")}));for(let t=0;t<e.length;t++)e[t].addEventListener("mouseover",(()=>{e[t].classList.add("hovered")})),e[t].addEventListener("mouseout",(()=>{e[t].classList.remove("hovered")}))}function burgerMenu(){const e=document.querySelector(".burger"),t=document.querySelector(".header-menu");e.addEventListener("click",(()=>{t.classList.contains("active")?(t.classList.remove("active"),e.classList.remove("active-burger")):(t.classList.add("active"),e.classList.add("active-burger"))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(t.classList.remove("active"),e.classList.remove("active-burger"))}))}function accordion(){document.querySelectorAll(".accordion__item-trigger").forEach((e=>{e.addEventListener("click",(()=>{const t=e.parentNode;t.classList.contains("accordion__item-active")?t.classList.remove("accordion__item-active"):(document.querySelectorAll(".accordion__item").forEach((e=>e.classList.remove("accordion__item-active"))),t.classList.add("accordion__item-active"))}))}))}headerMenu(),burgerMenu(),accordion();